import React, { useState } from 'react';
import './HomePage.css';  // Import the CSS file

function HomePage() {
  const [formData, setFormData] = useState({
    purchaseAmount: '',
    paymentMethod: '',
    year: '',
    itemPurchased: ''
  });

  const [prediction, setPrediction] = useState(null);

  const items = [
    'Belt', 'Skirt', 'Shorts', 'Pants', 'Tank Top', 'T-shirt', 'Pajamas', 'Camisole', 'Loafers', 'Hoodie', 'Poncho', 'Socks', 'Kimono', 'Blouse',
    'Wallet', 'Handbag', 'Cardigan', 'Vest', 'Sneakers', 'Onesie', 'Backpack', 'Blazer', 'Umbrella', 'Raincoat', 'Trench Coat', 'Hat', 
    'Sunglasses', 'Coat', 'Scarf', 'Swimsuit', 'Boots', 'Jacket', 'Romper', 'Flannel Shirt', 'Leggings', 'Sweater', 'Overalls', 'Tunic',
    'Polo Shirt', 'Gloves', 'Jeans', 'Slippers', 'Trousers', 'Jumpsuit', 'Dress', 'Tie', 'Sandals', 'Bowtie', 'Flip-Flops', 'Sun Hat'
  ];

  const paymentMethods = ['Credit Card', 'Cash'];

  const handleSubmit = async (e) => {
    e.preventDefault();

    const inputObj = {
      'Purchase Amount (USD)': parseFloat(formData.purchaseAmount),
      'Payment Method': formData.paymentMethod,
      'Year': parseInt(formData.year),
      'Item Purchased': formData.itemPurchased
    };

    try {
      const response = await fetch('http://localhost:5000/predict', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(inputObj)  // Sending the object directly
      });

      if (response.ok) {
        const data = await response.json();
        setPrediction(data.prediction ? `Predicted Review Rating: ${data.prediction}` : 'No prediction returned.');
      } else {
        setPrediction('Failed to fetch prediction.');
      }
    } catch (error) {
      console.error(error);
      setPrediction('Error occurred while fetching prediction.');
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value
    }));
  };

  return (
    <div className="container">
      <form className="form-container" onSubmit={handleSubmit}>
        <h2>Predict Product Review</h2>
        {/* Your form inputs go here */}
      </form>
      {prediction !== null && (
        <div className="prediction-result">
          <h3>{prediction}</h3>
        </div>
      )}
    </div>
  );
}

export default HomePage;
