import React, { useState } from 'react';
import axios from 'axios';

const PhonePage = () => {
  const [phoneNumber, setPhoneNumber] = useState('');

  const handlePhoneSubmit = async (e) => {
    e.preventDefault();
    const email = localStorage.getItem('email');
    try {
      // You can send the phone number to your backend here if needed
      console.log('Phone number submitted:', phoneNumber);
      alert('Phone number submitted successfully');
      // Redirect to another page after phone number submission
      window.location.href = '/secure-page';
    } catch (error) {
      console.error('Phone number submission error', error);
    }
  };

  return (
    <div>
      <h2>Phone Number</h2>
      <form onSubmit={handlePhoneSubmit}>
        <input
          type="text"
          value={phoneNumber}
          onChange={(e) => setPhoneNumber(e.target.value)}
          placeholder="Enter phone number"
          required
        />
        <button type="submit">Submit Phone Number</button>
      </form>
    </div>
  );
};

export default PhonePage;
