import React, { useState } from 'react';
import axios from 'axios';

function HomePage() {
  const [formData, setFormData] = useState({
    purchase_amount: '',
    payment_method: '',
    year: '',
    month: '',
    day: ''
  });

  const [prediction, setPrediction] = useState(null);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    axios
      .post('http://localhost:5001/predict', formData)
      .then((response) => {
        setPrediction(response.data.predicted_rating);
      })
      .catch((error) => {
        console.error('Error fetching prediction:', error);
      });
  };

  return (
    <div>
      <h1>Review Rating Predictor</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Purchase Amount (USD):</label>
          <input type="number" name="purchase_amount" value={formData.purchase_amount} onChange={handleChange} required />
        </div>
        <div>
          <label>Payment Method (1 = Credit, 0 = Debit):</label>
          <input type="number" name="payment_method" value={formData.payment_method} onChange={handleChange} required />
        </div>
        <div>
          <label>Year:</label>
          <input type="number" name="year" value={formData.year} onChange={handleChange} required />
        </div>
        <div>
          <label>Month:</label>
          <input type="number" name="month" value={formData.month} onChange={handleChange} required />
        </div>
        <div>
          <label>Day:</label>
          <input type="number" name="day" value={formData.day} onChange={handleChange} required />
        </div>
        <button type="submit">Predict Rating</button>
      </form>

      {prediction && (
        <div>
          <h2>Predicted Review Rating: {prediction}</h2>
        </div>
      )}
    </div>
  );
}

export default HomePage;
