// src/App.js

import React, { useState } from 'react';
import axios from 'axios';

function App() {
  // Form state
  const [formData, setFormData] = useState({
    purchase_amount: '',
    payment_method: '',
    year: '',
    month: '',
    day: '',
    item_purchased: ''
  });

  // State to hold the predicted rating
  const [predictedRating, setPredictedRating] = useState(null);
  const [error, setError] = useState(null);

  // Handle input change
  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();

    // Make POST request to Flask API
    axios.post('http://localhost:5001/predict', formData)
      .then((response) => {
        setPredictedRating(response.data.predicted_rating);
        setError(null);
      })
      .catch((err) => {
        setError(err.response?.data?.error || "An error occurred");
        setPredictedRating(null);
      });
  };

  return (
    <div className="App">
      <h1>Review Rating Predictor</h1>

      <form onSubmit={handleSubmit}>
        <div>
          <label>Purchase Amount: </label>
          <input
            type="number"
            name="purchase_amount"
            value={formData.purchase_amount}
            onChange={handleChange}
            required
          />
        </div>

        <div>
          <label>Payment Method: </label>
          <input
            type="text"
            name="payment_method"
            value={formData.payment_method}
            onChange={handleChange}
            required
          />
        </div>

        <div>
          <label>Year: </label>
          <input
            type="number"
            name="year"
            value={formData.year}
            onChange={handleChange}
            required
          />
        </div>

        <div>
          <label>Month: </label>
          <input
            type="number"
            name="month"
            value={formData.month}
            onChange={handleChange}
            required
          />
        </div>

        <div>
          <label>Day: </label>
          <input
            type="number"
            name="day"
            value={formData.day}
            onChange={handleChange}
            required
          />
        </div>

        <div>
          <label>Item Purchased: </label>
          <input
            type="text"
            name="item_purchased"
            value={formData.item_purchased}
            onChange={handleChange}
            required
          />
        </div>

        <button type="submit">Predict Rating</button>
      </form>

      {predictedRating !== null && (
        <div>
          <h2>Predicted Rating: {predictedRating}</h2>
        </div>
      )}

      {error && (
        <div>
          <h2>Error: {error}</h2>
        </div>
      )}
    </div>
  );
}

export default App;
