import React, { useState } from 'react';

function HomePage() {
  // State to store user input
  const [formData, setFormData] = useState({
    purchaseAmount: '',
    paymentMethod: '',  // This will be a string instead of number
    year: '',
    itemPurchased: '' // Name of the item purchased
  });

  // List of all possible items
  const items = [
    'Belt', 'Skirt', 'Shorts', 'Pants', 'Tank Top', 'T-shirt', 'Pajamas', 'Camisole', 'Loafers', 'Hoodie', 'Poncho', 'Socks', 'Kimono', 'Blouse',
     'Wallet', 'Handbag', 'Cardigan', 'Vest', 'Sneakers', 'Onesie', 'Backpack', 'Blazer', 'Umbrella', 'Raincoat', 'Trench Coat', 'Hat', 
     'Sunglasses', 'Coat', 'Scarf', 'Swimsuit', 'Boots', 'Jacket', 'Romper', 'Flannel Shirt', 'Leggings', 'Sweater', 'Overalls', 'Tunic',
     'Polo Shirt', 'Gloves', 'Jeans', 'Slippers', 'Trousers', 'Jumpsuit', 'Dress', 'Tie', 'Sandals', 'Bowtie', 'Flip-Flops', 'Sun Hat'
  ];

  const paymentMethods = ['Credit Card', 'Cash', 'Debit Card', 'PayPal'];

  // Function to handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    // Convert form data to the backend format
    const requestData = {
      "Purchase Amount (USD)": parseFloat(formData.purchaseAmount),
      "Payment Method": formData.paymentMethod,  // Send as a string (e.g., "Credit Card")
      "Year": parseInt(formData.year),
      "Item Purchased": formData.itemPurchased // Send only the item name
    };

    // Send a POST request to the backend
    try {
      const response = await fetch('http://localhost:5000/predict', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(requestData)
      });

      const data = await response.json();
      alert(`Predicted Review Rating: ${data.prediction}`);
    } catch (error) {
      console.error('Error:', error);
    }
  };

  // Function to handle input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value
    }));
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>Purchase Amount (USD):</label>
        <input
          type="number"
          name="purchaseAmount"
          value={formData.purchaseAmount}
          onChange={handleChange}
          required
        />
      </div>
      <div>
        <label>Payment Method:</label>
        <select
          name="paymentMethod"
          value={formData.paymentMethod}
          onChange={handleChange}
          required
        >
          <option value="">Select Payment Method</option>
          {paymentMethods.map((method, index) => (
            <option key={index} value={method}>
              {method}
            </option>
          ))}
        </select>
      </div>
      <div>
        <label>Year:</label>
        <input
          type="number"
          name="year"
          value={formData.year}
          onChange={handleChange}
          required
        />
      </div>
      <div>
        <label>Item Purchased:</label>
        <select
          name="itemPurchased"
          value={formData.itemPurchased}
          onChange={handleChange}
          required
        >
          <option value="">Select an item</option>
          {items.map((item, index) => (
            <option key={index} value={item}>
              {item}
            </option>
          ))}
        </select>
      </div>
      <button type="submit">Predict Review</button>
    </form>
  );
}

export default HomePage;
