import React, { useState } from 'react';

function HomePage() {
  // State to store user input
  const [formData, setFormData] = useState({
    purchaseAmount: '',
    paymentMethod: '',
    year: '',
    itemPurchased: ''
  });

  // List of all possible items
  const items = [
    'Belt', 'Skirt', 'Shorts', 'Pants', 'Tank Top', 'T-shirt', 'Pajamas', 'Camisole', 'Loafers', 'Hoodie', 'Poncho', 'Socks', 'Kimono', 'Blouse',
    'Wallet', 'Handbag', 'Cardigan', 'Vest', 'Sneakers', 'Onesie', 'Backpack', 'Blazer', 'Umbrella', 'Raincoat', 'Trench Coat', 'Hat', 
    'Sunglasses', 'Coat', 'Scarf', 'Swimsuit', 'Boots', 'Jacket', 'Romper', 'Flannel Shirt', 'Leggings', 'Sweater', 'Overalls', 'Tunic',
    'Polo Shirt', 'Gloves', 'Jeans', 'Slippers', 'Trousers', 'Jumpsuit', 'Dress', 'Tie', 'Sandals', 'Bowtie', 'Flip-Flops', 'Sun Hat'
  ];

  const paymentMethods = ['Credit Card', 'Cash'];

  // Function to handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    // Prepare input array (1D array)
    const inputArray = new Array(items.length + 3).fill(0); // Initialize with zeros

    // Handle purchased item
    const itemIndex = items.indexOf(formData.itemPurchased);
    if (itemIndex !== -1) {
      inputArray[itemIndex] = 1; // Set the purchased item to true (1)
    }

    // Handle payment method encoding
    const paymentIndex = formData.paymentMethod === 'Credit Card' ? 1 : 0; // 1 for Credit Card, 0 for Cash
    inputArray[items.length] = paymentIndex; // Add payment method at the end
    inputArray[items.length + 1] = parseFloat(formData.purchaseAmount); // Add purchase amount
    inputArray[items.length + 2] = parseInt(formData.year); // Add year

    // Send a POST request to the backend
    try {
      const response = await fetch('http://localhost:5000/predict', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(inputArray) // Send the input array directly
      });

      const data = await response.json();
      alert(`Predicted Review Rating: ${data.prediction}`);
    } catch (error) {
      console.error('Error:', error);
    }
  };

  // Function to handle input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value
    }));
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>Purchase Amount (USD):</label>
        <input
          type="number"
          name="purchaseAmount"
          value={formData.purchaseAmount}
          onChange={handleChange}
          required
        />
      </div>
      <div>
        <label>Payment Method:</label>
        <select
          name="paymentMethod"
          value={formData.paymentMethod}
          onChange={handleChange}
          required
        >
          <option value="">Select Payment Method</option>
          {paymentMethods.map((method, index) => (
            <option key={index} value={method}>
              {method}
            </option>
          ))}
        </select>
      </div>
      <div>
        <label>Year:</label>
        <input
          type="number"
          name="year"
          value={formData.year}
          onChange={handleChange}
          required
        />
      </div>
      <div>
        <label>Item Purchased:</label>
        <select
          name="itemPurchased"
          value={formData.itemPurchased}
          onChange={handleChange}
          required
        >
          <option value="">Select an item</option>
          {items.map((item, index) => (
            <option key={index} value={item}>
              {item}
            </option>
          ))}
        </select>
      </div>
      <button type="submit">Predict Review</button>
    </form>
  );
}

export default HomePage;
