import React, { useState } from 'react';
import axios from 'axios';

const OtpVerificationPage = () => {
  const [otp, setOtp] = useState('');
  const [verificationType, setVerificationType] = useState('email'); // 'email' or 'phone'
  
  const handleOtpSubmit = async (e) => {
    e.preventDefault();
    
    const endpoint = verificationType === 'email' 
      ? 'http://localhost:5000/verify-email-otp'
      : 'http://localhost:5000/verify-phone-otp';
    
    const email = localStorage.getItem('email');
    const phoneNumber = localStorage.getItem('phoneNumber'); // Ensure phoneNumber is set when required
    
    try {
      const response = await axios.post(endpoint, {
        email: verificationType === 'email' ? email : undefined,
        phoneNumber: verificationType === 'phone' ? phoneNumber : undefined,
        otp
      });
      
      if (response.data.success) {
        if (verificationType === 'email') {
          // Redirect to PhonePage after successful email OTP verification
          window.location.href = '/phone';
        } else {
          // Redirect to HomePage after successful phone OTP verification
          window.location.href = '/home';
        }
      } else {
        alert('Invalid OTP');
      }
    } catch (error) {
      console.error('OTP verification error', error);
    }
  };

  return (
    <div>
      <h2>Verify OTP</h2>
      <form onSubmit={handleOtpSubmit}>
        <input
          type="text"
          value={otp}
          onChange={(e) => setOtp(e.target.value)}
          placeholder={verificationType === 'email' ? 'Enter Email OTP' : 'Enter Phone OTP'}
          required
        />
        <button type="submit">Verify OTP</button>
      </form>
    </div>
  );
};

export default OtpVerificationPage;
