import React, { useState } from 'react';
import './HomePage.css';

function HomePage() {
  const [formData, setFormData] = useState({
    purchaseAmount: '',
    paymentMethod: '',
    year: '',
    itemPurchased: ''
  });

  const [prediction, setPrediction] = useState(null);

  const items = [ /* ... (same items as before) ... */ ];

  const paymentMethods = ['Credit Card', 'Cash'];

  const handleSubmit = async (e) => {
    e.preventDefault();

    const inputObj = {
      'Purchase Amount (USD)': parseFloat(formData.purchaseAmount),
      'Payment Method': formData.paymentMethod,
      'Year': parseInt(formData.year),
      'Item Purchased': formData.itemPurchased
    };

    try {
      const response = await fetch('http://localhost:5000/predict', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(inputObj)
      });

      if (response.ok) {
        const data = await response.json();
        setPrediction(data.prediction ? `Predicted Review Rating: ${data.prediction}` : 'No prediction returned.');
      } else {
        setPrediction('Failed to fetch prediction.');
      }
    } catch (error) {
      console.error(error);
      setPrediction('Error occurred while fetching prediction.');
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevData => ({
      ...prevData,
      [name]: value
    }));
  };

  return (
    <div className="container">
      <form className="form-container" onSubmit={handleSubmit}>
        <h2>Predict Product Review</h2>
        <div className="form-group">
          <label>Purchase Amount (USD):</label>
          <input
            type="number"
            name="purchaseAmount"
            value={formData.purchaseAmount}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label>Payment Method:</label>
          <select
            name="paymentMethod"
            value={formData.paymentMethod}
            onChange={handleChange}
            required
          >
            <option value="">Select Payment Method</option>
            {paymentMethods.map((method, index) => (
              <option key={index} value={method}>
                {method}
              </option>
            ))}
          </select>
        </div>
        <div className="form-group">
          <label>Year:</label>
          <input
            type="number"
            name="year"
            value={formData.year}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label>Item Purchased:</label>
          <select
            name="itemPurchased"
            value={formData.itemPurchased}
            onChange={handleChange}
            required
          >
            <option value="">Select an item</option>
            {items.map((item, index) => (
              <option key={index} value={item}>
                {item}
              </option>
            ))}
          </select>
        </div>
        <button className="submit-button" type="submit">Predict Review</button>
      </form>

      {prediction !== null && (
        <div className="prediction-result">
          <h3>{prediction}</h3>
        </div>
      )}
    </div>
  );
}

export default HomePage;
